    id: '741',
    question_id: '183',
    order: '1',
    text: 'No, the Board strictly adheres to using ministry resources for the benefit of the organisation and its ministry purposes.',
    detailed: null,
    score: '10.00',
    is_triggering_action: '0',
    risk_id: '10',
